CREATE TABLE admin_users (username VARCHAR(64), p_word VARCHAR(64) NOT NULL, PRIMARY KEY(username)) ENGINE = INNODB;
CREATE TABLE majors (id INT AUTO_INCREMENT, major VARCHAR(32) NOT NULL, is_visible TINYINT NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE projects (id INT AUTO_INCREMENT, title VARCHAR(64), organisation VARCHAR(64), description TEXT, has_additional_info TINYINT, major_ids VARCHAR(64), skill_set_ids VARCHAR(64), year INT NOT NULL, proj_num INT NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE project_allocations (project_id INT, snum1 INT, snum2 INT, snum3 INT, snum4 INT, snum5 INT, snum6 INT, PRIMARY KEY(project_id)) ENGINE = INNODB;
CREATE TABLE skill_sets (id INT AUTO_INCREMENT, area VARCHAR(64) NOT NULL, is_visible TINYINT NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE student_users (snum INT, p_word VARCHAR(64) NOT NULL, first_name VARCHAR(64), last_name VARCHAR(64), pass_fail_pm TINYINT, major_ids VARCHAR(32), gpa FLOAT(18, 2), proj_pref1 INT, proj_pref2 INT, proj_pref3 INT, proj_pref4 INT, proj_pref5 INT, skill_set_ids VARCHAR(32), y_stu_pref1 INT, y_stu_pref2 INT, y_stu_pref3 INT, y_stu_pref4 INT, y_stu_pref5 INT, n_stu_pref1 INT, n_stu_pref2 INT, n_stu_pref3 INT, n_stu_pref4 INT, n_stu_pref5 INT, proj_just1 TEXT, proj_just2 TEXT, proj_just3 TEXT, proj_just4 TEXT, proj_just5 TEXT, PRIMARY KEY(snum)) ENGINE = INNODB;
